,p_plug_new_grid_row=>false
,p_plug_display_point=>'BODY'
,p_plug_source=>wwv_flow_string.join(wwv_flow_t_varchar2(
'WITH hour_sequence AS',
'(',
'SELECT ',
'    TRUNC((SYSDATE - ROWNUM/24),''HH'') hr,',
'    ROWNUM id',
'FROM DUAL',
'CONNECT BY ROWNUM <=5',
'),',
'total_counts AS ',
'(',
'    SELECT',
'        ''CMS''                     db_name,',
'        COUNT(*)                  order_count,',
'        COUNT(distinct ord_id)    dist_order_count,',
'        COUNT(processed_dtm)      processed_count',
'    FROM  w_ii_web_ord_hdr@pmm',
'    UNION ALL',
'    SELECT',
'        ''OMS''                     db_name,',
'        COUNT(*)                  order_count,',
'        COUNT(distinct ord_id)    dist_order_count,',
'        COUNT(processed_dtm)      processed_count',
'    FROM  w_ii_web_ord_hdr',
'    UNION ALL',
'    SELECT',
'        ''WMS''                     db_name,',
'        COUNT(*)                  order_count,',
'        COUNT(distinct ord_id)    dist_order_count,',
'        COUNT(processed_dtm)      processed_count',
'    FROM  w_ii_web_ord_hdr@wms',
'),',
'cms_last_five_hour_counts AS (',
'    SELECT',
'        TRUNC(insert_dtm,''HH'')             insert_dt,',
'        COUNT(*)                           order_count',
'    FROM  w_ii_web_ord_hdr@pmm',
'    WHERE insert_dtm >= SYSDATE-5/24',
'    GROUP BY TRUNC(insert_dtm,''HH'')',
'),',
'oms_last_five_hour_counts AS (',
'    SELECT',
'        TRUNC(insert_dtm,''HH'')             insert_dt,',
'        COUNT(*)                           order_count',
'    FROM  w_ii_web_ord_hdr',
'    WHERE insert_dtm >= SYSDATE-5/24',
'    GROUP BY TRUNC(insert_dtm,''HH'')',
'),',
'wms_last_five_hour_counts AS (',
'    SELECT',
'        TRUNC(insert_dtm,''HH'')             insert_dt,',
'        COUNT(*)                           order_count',
'    FROM  w_ii_web_ord_hdr@wms',
'    WHERE insert_dtm >= SYSDATE-5/24',
'    GROUP BY TRUNC(insert_dtm,''HH'')',
'),',
'error_counts As (',
'    SELECT',
'        ''CMS''                     db_name,',
'        SUM(',
'            CASE scope',
'            WHEN ''cms_web_order.p_process_batch''',
'            THEN 1',
'            ELSE 0',
'            END ',
'        )                         batch_error_count,',
'        SUM(',
'            CASE scope',
'            WHEN ''s_load_order_util.p_accept_order_api''',
'            THEN 1',
'            ELSE 0',
'            END ',
'        )                         api_error_count',
'    FROM',
'        logger_logs@pmm',
'    WHERE',
'        scope in(',
'            ''cms_web_order.p_process_batch'',',
'            ''s_load_order_util.p_accept_order_api''',
'        )',
'        AND   logger_level = 2',
'        AND   time_stamp > sysdate-1',
'    UNION ALL ',
'    SELECT',
'        ''OMS''                     db_name,',
'        SUM(',
'            CASE scope',
'            WHEN ''oms_web_order.p_process_batch''',
'            THEN 1',
'            ELSE 0',
'            END ',
'        )                         batch_error_count,',
'        SUM(',
'            CASE scope',
'            WHEN ''s_load_order_util.p_accept_order_api''',
'            THEN 1',
'            ELSE 0',
'            END ',
'        )                         api_error_count',
'    FROM',
'        logger_logs',
'    WHERE',
'        scope in(',
'            ''oms_web_order.p_process_batch'',',
'            ''s_load_order_util.p_accept_order_api''',
'        )',
'        AND   logger_level = 2',
'        AND   time_stamp > sysdate-1',
'    UNION ALL',
'    SELECT',
'        ''WMS''                     db_name,',
'        SUM(',
'            CASE scope',
'            WHEN ''wms_web_order.p_process_batch''',
'            THEN 1',
'            ELSE 0',
'            END ',
'        )                         batch_error_count,',
'        SUM(',
'            CASE scope',
'            WHEN ''s_load_order_util.p_accept_order_api''',
'            THEN 1',
'            ELSE 0',
'            END ',
'        )                         api_error_count',
'    FROM',
'        logger_logs@wms',
'    WHERE',
'        scope in(',
'            ''wms_web_order.p_process_batch'',',
'            ''s_load_order_util.p_accept_order_api''',
'        )',
'        AND   logger_level = 2',
'        AND   time_stamp > sysdate-1',
'),',
'bar_chart_header AS (',
'    SELECT',
'        ''CMS''                                 cms_db_name,',
'        ''OMS''                                 oms_db_name,',
'        ''WMS''                                 wms_db_name,',
'        ''cms/oms/wms''                         all_db_name,',
'        ''chart-bar''                           chart_bar,',
'        ''Orders count - Last five hours''      bar_chart_title,',
'        ''Curr=Present Hour, Curr-1=Last Hour and so on..'' card_footer,',
'        ''background: linear-gradient(60deg, hsl(23, 55%, 60%), hsl(23, 50%, 60%));box-shadow: 0 12px 20px -10px rgba(230, 230, 230, 0.28), 0 4px 20px 0px rgba(0, 0, 0, 0.12), 0 7px 8px -5px rgba(230, 230, 230, 0.2);''  CARD_HEADER_STYLE',
'    FROM  DUAL',
'),',
'CMS_ORD_COUNT  AS (',
'    SELECT ',
'        LISTAGG(NVL(order_count,0), '','') ',
'            WITHIN GROUP (ORDER BY hs.id DESC) ',
'    FROM   cms_last_five_hour_counts  clfhs',
'        RIGHT JOIN hour_sequence hs',
'            ON (clfhs.insert_dt = hs.hr)',
'), ',
'OMS_ORD_COUNT  AS (',
'    SELECT ',
'        LISTAGG(NVL(order_count,0), '','') ',
'            WITHIN GROUP (ORDER BY hs.id DESC) ',
'    FROM   oms_last_five_hour_counts olfhs',
'        RIGHT JOIN hour_sequence hs',
'            ON (olfhs.insert_dt = hs.hr)',
'), ',
'WMS_ORD_COUNT  AS (',
'    SELECT ',
'        LISTAGG(NVL(order_count,0), '','') ',
'            WITHIN GROUP (ORDER BY hs.id DESC) ',
'    FROM   wms_last_five_hour_counts wlfhs',
'        RIGHT JOIN hour_sequence hs',
'            ON (wlfhs.insert_dt = hs.hr)',
'),',
'ALL_ERR_COUNT  AS (',
'    SELECT ',
'        LISTAGG(batch_error_count, '','') ',
'            WITHIN GROUP (ORDER BY db_name) batch_err,',
'        LISTAGG(api_error_count, '','') ',
'            WITHIN GROUP (ORDER BY db_name) api_err',
'    FROM   error_counts ',
')',
'/* Small KPI Cards */',
'SELECT ',
'    ''icon''                                         AS CARD_TYPE, ',
'    DECODE(db_name, ',
'        ''CMS'', ''fa-database'', ',
'        ''OMS'', ''fa-home'', ',
'        ''fa-cart-arrow-down'')                      AS CARD_ICON, ',
'    NULL                                           AS CARD_ICON_COLOR, ',
'    NULL                                           AS CARD_HEADER_STYLE,',
'    db_name                                        AS CARD_TITLE, ',
'    CASE ',
'    WHEN order_count < 10 THEN',
'    ''<span style="color:#920505;">''',
'    WHEN order_count < 500 THEN',
'    ''<span style="color:#935805;">''',
'    ELSE',
'    ''<span style="color:#059118;">''',
'    END ||order_count||''</span>''                    AS CARD_VALUE, ',
'    ''Staging Count: Total=''||order_count||',
